mapper.select:

SELECT ichild_age
FROM t_user_child
where iuser = #{iuser}

SELECT product_pic
FROM t_product_pics
WHERE iproduct = #{iproduct}

SELECT ireview,review_pic as reviewPic
FROM t_review_pics
WHERE ireview in (

    #{ireview}

)

SELECT A.iproduct, M.imiddle, M.imain, A.product_nm, A.price, A.rc_fl, A.pop_fl, A.new_fl, A.rep_pic, COUNT(DISTINCT R.iorder) AS reviewCnt, IF(COUNT(L.iuser) > 0, 1, 0) AS likeProduct FROM t_product A LEFT JOIN t_middle_category M ON A.candidate_key = M.candidate_key LEFT JOIN t_review R ON A.iproduct = R.iproduct LEFT JOIN t_wish_list L ON A.iproduct = L.iproduct
A.del_fl = 0 and M.imiddle = #{imiddle} AND M.imain = #{imain} M.imain = #{imain} M.imiddle = #{imiddle}
GROUP BY A.iproduct ORDER BY A.created_at DESC A.price desc A.price ASC LIMIT #{startIdx},#{rowCount}

mapper.insert:
INSERT INTO t_basket
SET iuser = #{iuser}
,iproduct = #{iproduct}
,product_cnt = #{productCnt}
,price = (select price FROM t_product WHERE iproduct =#{iproduct}) * #{productCnt}

mapper.select:
SELECT product_cnt
FROM t_basket
where iuser = #{iuser}
and iproduct = #{iproduct}

mapper.update:
UPDATE t_basket
SET
product_cnt = #{productCnt},
price = (SELECT price FROM t_product WHERE iproduct = #{iproduct}) * #{productCnt}
WHERE
iuser = #{iuser} AND iproduct = #{iproduct}

mapper.delete:
DELETE FROM t_basket
WHERE iproduct IN

    #{iproduct}

AND iuser =#{iuser}

mapper.select:
SELECT A.product_nm, A.iproduct, A.rep_pic, A.price, A.product_details,
if(B.iuser IS NULL, 0, 1) AS likeProduct
FROM t_product A
LEFT JOIN t_wish_list B ON A.iproduct = B.iproduct
AND B.iuser = #{iuser}
WHERE A.iproduct = #{iproduct}

